riot.tag2("ftl-menu-button",'<a onclick="{click}" class="{classList}" tabindex="0" aria-controls="{aria}" riot-style="{style}"><span><i if="{icon}" class="{icon}" riot-style="{iconStyle}"></i></span> {text}</a>',"","",function(b){var a=this;a.text=b.text;a.classList=b.classList===undefined?"clearfix btn btn-sm btn-space btn-primary":b.classList;a.href=b.href;a.aria=b.aria;a.icon=b.icon===undefined?"":b.icon;a.iconStyle=b.iconStyle===undefined?"color:#ffffff;":b.iconStyle;a.style="padding-bottom: 5px; padding-top: 5px;color:#ffffff;";a.on("mount",function(){if(a.root){a.root.setAttribute("style",a.style)}});this.click=function(c){window.location.href=a.href}.bind(this)});